import React, { useState } from "react";

export default function CheckoutForm() {
  const [formData, setFormData] = useState({
    cardNumber: "",
    expiry: "",
    cvv: "",
    amount: "",
    auth3ds: "Y", // ✅ default to Y, but user can change to N
    currency: "840", // ✅ default USD (840), KHR is 116
  });

  const [resultMessage, setResultMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setResultMessage(null);

    // ✅ Prepare payload: always capture "N", include currency + auth3ds from user
    const payload = {
      ...formData,
      capture: "N",
    };

    console.log("✅ Sending payload:", payload);

    try {
      const res = await fetch("http://localhost:5001/api/payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (data.data && data.data.status === "Approved") {
        setResultMessage(
          `✅ Payment Approved! Auth Code: ${data.data.authnumber}`
        );
      } else {
        setResultMessage(
          `❌ Payment Failed. Status: ${data.data?.status || "Unknown"}`
        );
      }
    } catch (error) {
      console.error("❌ Payment Error:", error);
      setResultMessage("❌ An error occurred while processing the payment.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white shadow-md rounded">
      <h2 className="text-xl font-semibold mb-4">Checkout</h2>

      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          className="w-full border p-2"
          name="cardNumber"
          placeholder="Card Number"
          value={formData.cardNumber}
          onChange={handleChange}
          required
        />
        <input
          className="w-full border p-2"
          name="expiry"
          placeholder="MM/YY"
          value={formData.expiry}
          onChange={handleChange}
          required
        />
        <input
          className="w-full border p-2"
          name="cvv"
          placeholder="CVV"
          value={formData.cvv}
          onChange={handleChange}
          required
        />
        <input
          className="w-full border p-2"
          name="amount"
          placeholder="Amount (e.g., 100)"
          value={formData.amount}
          onChange={handleChange}
          required
        />

        {/* ✅ Currency selector */}
        <label className="block">Currency:</label>
        <select
          className="w-full border p-2"
          name="currency"
          value={formData.currency}
          onChange={handleChange}
        >
          <option value="840">USD (840)</option>
          <option value="116">KHR (116)</option>
        </select>

        {/* ✅ 3DS selector */}
        <label className="block">3DS:</label>
        <select
          className="w-full border p-2"
          name="auth3ds"
          value={formData.auth3ds}
          onChange={handleChange}
        >
          <option value="Y">3DS Enabled (Y)</option>
          <option value="N">3DS Disabled (N)</option>
        </select>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          disabled={isLoading}
        >
          {isLoading ? "Processing..." : "Pay Now"}
        </button>
      </form>

      {resultMessage && (
        <div className="mt-4 p-2 border rounded text-center">
          {resultMessage}
        </div>
      )}
    </div>
  );
}
