import React, { useState } from "react";

export default function CaptureForm() {
  const [formData, setFormData] = useState({
    orderid: "",
    paymentid: "",
    amount: "",
    authnumber: "",
  });

  const [resultMessage, setResultMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setResultMessage(null);

    try {
      const res = await fetch("http://localhost:5001/api/capture", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (data && data.data && data.data.status === "Approved") {
        setResultMessage({
          type: "success",
          text: `‚úÖ Capture Approved! Auth Code: ${data.data.authnumber}`,
        });
      } else {
        setResultMessage({
          type: "error",
          text: `‚ùå Capture Failed: ${
            data.data ? data.data.status : "Unknown error"
          }`,
        });
      }
    } catch (error) {
      console.error("Capture API error:", error);
      setResultMessage({
        type: "error",
        text: "‚ùå An error occurred during capture.",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div
      style={{
        maxWidth: "400px",
        margin: "50px auto",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h2 style={{ textAlign: "center" }}>üí≥ Capture Payment</h2>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "column", gap: "15px" }}
      >
        <input
          type="text"
          name="orderid"
          placeholder="Order ID"
          value={formData.orderid}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="text"
          name="paymentid"
          placeholder="Payment ID"
          value={formData.paymentid}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="text"
          name="amount"
          placeholder="Amount"
          value={formData.amount}
          onChange={handleChange}
          required
          style={inputStyle}
        />
        <input
          type="text"
          name="authnumber"
          placeholder="Auth Number"
          value={formData.authnumber}
          onChange={handleChange}
          required
          style={inputStyle}
        />

        <button type="submit" disabled={isLoading} style={buttonStyle}>
          {isLoading ? "Capturing..." : "Capture Payment"}
        </button>
      </form>

      {resultMessage && (
        <div
          style={{
            marginTop: "20px",
            padding: "10px",
            borderRadius: "5px",
            backgroundColor:
              resultMessage.type === "success" ? "#d4edda" : "#f8d7da",
            color: resultMessage.type === "success" ? "#155724" : "#721c24",
            textAlign: "center",
          }}
        >
          {resultMessage.text}
        </div>
      )}
    </div>
  );
}

// Simple styles
const inputStyle = {
  padding: "10px",
  borderRadius: "4px",
  border: "1px solid #ccc",
  fontSize: "14px",
};

const buttonStyle = {
  padding: "10px",
  borderRadius: "4px",
  border: "none",
  backgroundColor: "#4a90e2",
  color: "white",
  fontSize: "16px",
  cursor: "pointer",
};
