import React, { useState } from "react";

export default function CaptureForm() {
  const [formData, setFormData] = useState({
    orderid: "",
    paymentid: "",
    amount: "",
    authnumber: "",
  });

  const [resultMessage, setResultMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setResultMessage(null);

    try {
      const res = await fetch("http://localhost:5001/api/capture", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (data && data.data && data.data.status === "Approved") {
        setResultMessage(
          `✅ Capture Approved! Capture Auth Code: ${data.data.authnumber}`
        );
      } else {
        setResultMessage(
          `❌ Capture Failed: ${data.data ? data.data.status : "Unknown error"}`
        );
      }
    } catch (error) {
      console.error("Capture API error:", error);
      setResultMessage("❌ An error occurred during capture.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto" }}>
      <h2>Capture Payment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Order ID:</label>
          <input
            type="text"
            name="orderid"
            value={formData.orderid}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Payment ID:</label>
          <input
            type="text"
            name="paymentid"
            value={formData.paymentid}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="text"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Auth Number:</label>
          <input
            type="text"
            name="authnumber"
            value={formData.authnumber}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Capturing..." : "Capture Payment"}
        </button>
      </form>

      {resultMessage && (
        <div style={{ marginTop: "20px" }}>
          <strong>{resultMessage}</strong>
        </div>
      )}
    </div>
  );
}
